def score(trio):
	total = 0
	for char in trio:
		if char == "B": total += 1
		if char == "C": total += 3
		if char == "D": total += 5
	if "x" not in trio:
		total += 6
	if trio.count("x") == 1:
		total += 2
	return total

puzz = ""

total = 0
while puzz:
	pair = puzz[0:3]
	total += score(pair)
	puzz = puzz[3:]
print(total)
